

-- uncomment this if you already created the database and you
-- want to start over (it deletes all the tables and data):
DROP SCHEMA dnd;

CREATE SCHEMA dnd;

USE dnd;

CREATE TABLE spells
(
	sname varchar(20),
	srange int,
	duration varchar(35),
	slevel int,
	saving varchar(15),
	class varchar(60),
	school varchar(15),
	castingtime varchar(10),
	description varchar(1000),
    components varchar(50),
	PRIMARY KEY (sname)
);


